import React from 'react';

const GRID_SIZE = 50; // px
const COLS = 20;
const ROWS = 12;
const DURATION = 2.5; // seconds

export default function CyberWaveOverlay() {
  const squares = [];
  for (let y = 0; y < ROWS; y++) {
    for (let x = 0; x < COLS; x++) {
      // Calculate delay so the wave moves bottom-right to top-left
      const delay = ((COLS - x) + y) * 0.08;
      squares.push(
        <rect
          key={`${x}-${y}`}
          x={x * GRID_SIZE}
          y={y * GRID_SIZE}
          width={GRID_SIZE}
          height={GRID_SIZE}
          fill="#00fff7"
          fillOpacity="0"
        >
          <animate
            attributeName="fill-opacity"
            values="0;0.7;0"
            keyTimes="0;0.5;1"
            dur={`${DURATION}s`}
            begin={`${delay}s;wave.end+${delay}s`}
            repeatCount="indefinite"
            id={x === 0 && y === ROWS - 1 ? "wave" : undefined}
          />
        </rect>
      );
    }
  }

  return (
    <svg
      width="100vw"
      height="100vh"
      viewBox={`0 0 ${COLS * GRID_SIZE} ${ROWS * GRID_SIZE}`}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        pointerEvents: 'none',
        zIndex: 1,
        width: '100vw',
        height: '100vh',
        mixBlendMode: 'lighter',
      }}
    >
      {squares}
    </svg>
  );
}
