import React, { useEffect, useState } from 'react';

const API_URL = 'http://localhost:4000/api/builds';
const UPLOAD_URL = 'http://localhost:4000/api/builds';

function GunBuilds() {
  const [builds, setBuilds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showUpload, setShowUpload] = useState(false);
  const [form, setForm] = useState({ title: '', description: '', tags: '', image: null, password: '', category: '' });
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  // Modal state for image enlargement
  const [modalImage, setModalImage] = useState(null);
  const [openCategory, setOpenCategory] = useState(null);

  // Define categories
  const categories = [
    'ASSAULT RIFLES',
    'SUBMACHINE GUNS',
    'CARBINE',
    'MARKSMAN RIFLE',
    'BOLT-ACTION RIFLE',
    'SHOTGUN',
    'LIGHT MACHINE GUN',
    'PISTOL'
  ];

  // Group builds by category
  const buildsByCategory = categories.reduce((acc, cat) => {
    acc[cat] = builds.filter(b => (b.category || '').toUpperCase() === cat);
    return acc;
  }, {});

  useEffect(() => {
    fetchBuilds();
  }, []);

  async function fetchBuilds() {
    setLoading(true);
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setBuilds(data);
    } catch (e) {
      setBuilds([]);
    }
    setLoading(false);
  }

  function handleInput(e) {
    const { name, value, files } = e.target;
    setForm(f => ({ ...f, [name]: files ? files[0] : value }));
  }

  async function handleUpload(e) {
    e.preventDefault();
    setUploading(true);
    setError('');
    setSuccess('');
    const fd = new FormData();
    fd.append('title', form.title);
    fd.append('description', form.description);
    fd.append('tags', form.tags);
    fd.append('password', form.password);
    fd.append('category', form.category); // Add category to form data
    if (form.image) fd.append('image', form.image);
    try {
      const res = await fetch(UPLOAD_URL, { method: 'POST', body: fd });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Upload failed');
      setSuccess('Build uploaded!');
      setForm({ title: '', description: '', tags: '', image: null, password: '', category: '' }); // Reset category
      fetchBuilds();
    } catch (e) {
      setError(e.message);
    }
    setUploading(false);
  }

  return (
    <div className="min-h-screen py-20 px-4">
      <div className="max-w-5xl mx-auto">
        <div className="card-glow p-10 md:p-16 bg-card-bg/80 backdrop-blur-md rounded-2xl shadow-lg">
          <div className="flex justify-between items-center mb-10">
            <h1 className="text-4xl font-gaming font-bold glow-text">Imow's Gun Builds</h1>
            <button className="cyber-button" onClick={() => setShowUpload(v => !v)}>
              {showUpload ? 'Close Upload' : 'Admin Upload'}
            </button>
          </div>

          {showUpload && (
            <form className="card-glow p-8 mb-12 space-y-6" onSubmit={handleUpload}>
              <h2 className="text-2xl font-gaming mb-4">Upload New Build (Admin Only)</h2>
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <label className="block mb-2 text-sm">Title *</label>
                  <input name="title" value={form.title} onChange={handleInput} required className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-neon-blue" />
                </div>
                <div>
                  <label className="block mb-2 text-sm">Tags (comma separated)</label>
                  <input name="tags" value={form.tags} onChange={handleInput} className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-neon-blue" />
                </div>
                <div>
                  <label className="block mb-2 text-sm">Category *</label>
                  <select name="category" value={form.category} onChange={handleInput} required className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-neon-blue">
                    <option value="">Select Category</option>
                    {categories.map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                </div>
                <div className="md:col-span-2">
                  <label className="block mb-2 text-sm">Description</label>
                  <textarea name="description" value={form.description} onChange={handleInput} rows={2} className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-neon-blue" />
                </div>
                <div>
                  <label className="block mb-2 text-sm">Image *</label>
                  <input name="image" type="file" accept="image/*" onChange={handleInput} required className="w-full" />
                </div>
                <div>
                  <label className="block mb-2 text-sm">Admin Password *</label>
                  <input name="password" type="password" value={form.password} onChange={handleInput} required className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-neon-blue" />
                </div>
              </div>
              {error && <div className="text-red-400 font-bold">{error}</div>}
              {success && <div className="text-neon-green font-bold">{success}</div>}
              <button className="cyber-button mt-4" type="submit" disabled={uploading}>{uploading ? 'Uploading...' : 'Upload Build'}</button>
            </form>
          )}

          <h2 className="text-2xl font-gaming mb-6">Community Gallery</h2>
          {loading ? (
            <div className="text-center py-20 text-neon-blue animate-pulse">Loading builds...</div>
          ) : (
            <div className="flex flex-col gap-4">
              {categories.map(cat => (
                <div key={cat} className="mb-2">
                  <button
                    className={`w-full text-left px-6 py-3 font-gaming text-lg rounded-lg transition-all duration-200 ${openCategory === cat ? 'bg-neon-blue/10 text-neon-blue' : 'bg-gray-900 text-white hover:bg-neon-blue/5'}`}
                    onClick={() => setOpenCategory(openCategory === cat ? null : cat)}
                  >
                    {cat}
                  </button>
                  {openCategory === cat && (
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 mt-4">
                      {buildsByCategory[cat].length === 0 ? (
                        <div className="text-gray-400 col-span-full">No builds in this category.</div>
                      ) : (
                        buildsByCategory[cat].map(build => (
                          <div key={build.id} className="card-glow p-4 flex flex-col hover:scale-105 transition-transform duration-300">
                            <div className="aspect-video bg-gray-900 rounded-lg overflow-hidden mb-4 cursor-pointer" onClick={() => setModalImage(`http://localhost:4000${build.image}`)}>
                              <img src={`http://localhost:4000${build.image}`} alt={build.title} className="w-full h-full object-cover" />
                            </div>
                            <h3 className="font-gaming text-lg text-neon-blue mb-1">{build.title}</h3>
                            <div className="text-gray-400 text-xs mb-2">{new Date(build.createdAt).toLocaleString()}</div>
                            <div className="text-gray-300 text-sm mb-2 line-clamp-2">{build.description}</div>
                            <div className="flex flex-wrap gap-2 mt-auto">
                              {build.tags && build.tags.map((tag, i) => (
                                <span key={i} className="bg-gray-800 text-neon-blue px-2 py-1 rounded text-xs">{tag}</span>
                              ))}
                            </div>
                          </div>
                        ))
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      {/* Image Modal */}
      {modalImage && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80" onClick={() => setModalImage(null)}>
          <div className="relative w-full h-full flex items-center justify-center p-4" onClick={e => e.stopPropagation()}>
            <img src={modalImage} alt="Enlarged Build" className="max-h-screen w-auto h-auto rounded-lg shadow-lg object-contain" style={{maxWidth: '95vw'}} />
            <button className="absolute top-4 right-4 bg-neon-blue text-dark-bg rounded-full px-3 py-1 font-bold text-lg" onClick={() => setModalImage(null)}>
              ×
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default GunBuilds;
