import { useEffect } from 'react';

const GRID_SIZE = 50;
const COLS = 40; // More columns for full width
const ROWS = 20; // More rows for full height
const DURATION = 2.5; // seconds
const COLOR = '#00fff7';

function generateSVG() {
  let svg = `<svg xmlns='http://www.w3.org/2000/svg' width='${COLS * GRID_SIZE}' height='${ROWS * GRID_SIZE}' viewBox='0 0 ${COLS * GRID_SIZE} ${ROWS * GRID_SIZE}'>`;
  for (let y = 0; y < ROWS; y++) {
    for (let x = 0; x < COLS; x++) {
      svg += `<rect id='sq-${x}-${y}' x='${x * GRID_SIZE}' y='${y * GRID_SIZE}' width='${GRID_SIZE}' height='${GRID_SIZE}' fill='${COLOR}' fill-opacity='0'/>`;
    }
  }
  svg += '</svg>';
  return svg;
}

export default function AnimatedGridBackground() {
  useEffect(() => {
    // Inject SVG as background
    const svgString = generateSVG();
    const encoded = `url('data:image/svg+xml;utf8,${encodeURIComponent(svgString)}')`;
    document.body.style.backgroundImage = document.body.style.backgroundImage
      ? `${encoded},${document.body.style.backgroundImage}`
      : encoded;

    // Animation loop
    let frame = 0;
    let running = true;
    function animate() {
      for (let y = 0; y < ROWS; y++) {
        for (let x = 0; x < COLS; x++) {
          const el = document.getElementById(`sq-${x}-${y}`);
          if (el) {
            // Calculate wave delay
            const delay = ((COLS - x) + y) * 0.08;
            const t = ((frame / 60) - delay) % DURATION;
            let opacity = 0;
            if (t > 0 && t < DURATION / 2) {
              opacity = 0.7 * Math.sin((t / (DURATION / 2)) * Math.PI);
            }
            el.setAttribute('fill-opacity', opacity.toFixed(2));
          }
        }
      }
      if (running) requestAnimationFrame(animate);
    }
    animate();
    return () => { running = false; };
  }, []);
  return null;
}
