import React, { useState } from 'react';
import { Mail, CheckCircle, AlertCircle, Send, Users, Bell, Gift, Twitch, Twitter, Youtube, MessageCircle } from 'lucide-react';

const Newsletter = () => {
  const [email, setEmail] = useState('');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!email) {
      setError('Please enter your email address');
      return;
    }

    if (!validateEmail(email)) {
      setError('Please enter a valid email address');
      return;
    }

    setIsLoading(true);

    // Simulate API call
    setTimeout(() => {
      setIsLoading(false);
      setIsSubscribed(true);
      setEmail('');
    }, 2000);
  };

  const benefits = [
    {
      icon: Bell,
      title: "Stream Notifications",
      description: "Get notified when Im0w goes live"
    },
    {
      icon: Gift,
      title: "Exclusive Content",
      description: "Access to behind-the-scenes content"
    },
    {
      icon: Users,
      title: "Community Updates",
      description: "Stay updated with community events"
    }
  ];

  if (isSubscribed) {
    return (
      <section id="contact" className="py-20 relative">
        <div className="absolute inset-0 bg-gradient-to-b from-dark-bg via-darker-bg to-dark-bg"></div>
        
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
          <div className="card-glow p-12 text-center">
            <div className="w-20 h-20 bg-neon-green/20 rounded-full flex items-center justify-center mx-auto mb-6">
              <CheckCircle size={40} className="text-neon-green" />
            </div>
            <h2 className="text-4xl font-gaming font-bold text-neon-green mb-4">
              Welcome to the Community!
                </h2>
            <p className="text-xl text-gray-300 mb-8">
              You're now subscribed to the Im0w newsletter. Check your email for a welcome message and exclusive content.
            </p>
            <div className="space-y-4">
              <p className="text-gray-400">
                What's next:
              </p>
              <ul className="text-gray-300 space-y-2">
                <li>• Stream schedule and notifications</li>
                <li>• Tournament announcements</li>
                <li>• Exclusive behind-the-scenes content</li>
                <li>• Community event updates</li>
              </ul>
            </div>
            <button 
              onClick={() => setIsSubscribed(false)}
              className="cyber-button mt-8"
            >
              Subscribe Another Email
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="contact" className="py-20 relative">
      {/* Background Effects */}
      <div className="absolute inset-0 bg-gradient-to-b from-dark-bg via-darker-bg to-dark-bg"></div>
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-6xl font-gaming font-bold mb-6">
            <span className="glow-text">JOIN THE COMMUNITY</span>
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Never miss a stream, tournament, or community event. Subscribe to get exclusive updates, 
            behind-the-scenes content, and early access to announcements.
          </p>
        </div>

        <div className="grid lg:grid-cols-2 gap-16 items-center">
          {/* Left: Benefits */}
          <div className="space-y-8">
            <h3 className="text-3xl font-gaming text-neon-blue mb-8">
              What You'll Get
            </h3>
            <div className="space-y-6">
              {benefits.map((benefit, index) => (
                <div key={index} className="flex items-start space-x-4 group">
                  <div className="w-12 h-12 bg-neon-blue/20 rounded-lg flex items-center justify-center group-hover:bg-neon-blue/30 transition-colors duration-300">
                    <benefit.icon size={24} className="text-neon-blue" />
                  </div>
                  <div>
                    <h4 className="text-xl font-gaming font-bold text-white mb-2">
                      {benefit.title}
                    </h4>
                    <p className="text-gray-300 leading-relaxed">
                      {benefit.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>

            {/* Stats */}
            <div className="grid grid-cols-3 gap-6 pt-8">
              <div className="text-center">
                <div className="text-2xl font-gaming text-neon-green">50K+</div>
                <div className="text-sm text-gray-400">Subscribers</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-gaming text-neon-blue">100%</div>
                <div className="text-sm text-gray-400">Free</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-gaming text-neon-purple">24/7</div>
                <div className="text-sm text-gray-400">Updates</div>
              </div>
            </div>
          </div>

          {/* Right: Newsletter Form */}
          <div className="card-glow p-8">
            <div className="text-center mb-8">
              <div className="w-16 h-16 bg-neon-blue/20 rounded-full flex items-center justify-center mx-auto mb-4">
                <Mail size={32} className="text-neon-blue" />
              </div>
              <h3 className="text-2xl font-gaming font-bold text-white mb-2">
                Subscribe to Newsletter
              </h3>
              <p className="text-gray-300">
                Join thousands of fans getting exclusive updates
              </p>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                  Email Address
                </label>
                <div className="relative">
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your.email@example.com"
                    className={`w-full px-4 py-3 bg-gray-800 border rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 transition-all duration-300 ${
                      error 
                        ? 'border-red-500 focus:ring-red-500/50' 
                        : 'border-gray-600 focus:border-neon-blue focus:ring-neon-blue/50'
                    }`}
                    disabled={isLoading}
                  />
                  {error && (
                    <div className="flex items-center space-x-2 mt-2 text-red-400 text-sm">
                      <AlertCircle size={16} />
                      <span>{error}</span>
                    </div>
                  )}
                </div>
              </div>

              <button
                type="submit"
                disabled={isLoading}
                className={`w-full py-3 px-6 rounded-lg font-gaming font-bold transition-all duration-300 flex items-center justify-center ${
                  isLoading
                    ? 'bg-gray-600 text-gray-400 cursor-not-allowed'
                    : 'cyber-button'
                }`}
              >
                {isLoading ? (
                  <>
                    <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Subscribing...
                  </>
                ) : (
                  <>
                    <Send size={20} className="mr-2" />
                    Subscribe Now
                  </>
                )}
              </button>
            </form>

            <div className="mt-6 text-center">
              <p className="text-xs text-gray-400">
                By subscribing, you agree to receive emails from Im0w. 
                You can unsubscribe at any time.
              </p>
            </div>

            {/* Social Links */}
            <div className="mt-8 pt-8 border-t border-gray-700">
              <p className="text-center text-gray-300 mb-4">
                Or connect with us on social media:
              </p>
              <div className="flex justify-center space-x-4">
                <a href="https://www.twitch.tv/imow" target="_blank" rel="noopener noreferrer" className="w-10 h-10 bg-neon-blue hover:bg-neon-blue/80 rounded-lg flex items-center justify-center transition-colors duration-300">
                  <Twitch size={20} className="text-dark-bg" />
                </a>
                <a href="https://x.com/ImOwFromYT" target="_blank" rel="noopener noreferrer" className="w-10 h-10 bg-neon-blue hover:bg-neon-blue/80 rounded-lg flex items-center justify-center transition-colors duration-300">
                  <Twitter size={20} className="text-dark-bg" />
                </a>
                <a href="https://www.youtube.com/@ImOwPC" target="_blank" rel="noopener noreferrer" className="w-10 h-10 bg-neon-blue hover:bg-neon-blue/80 rounded-lg flex items-center justify-center transition-colors duration-300">
                  <Youtube size={20} className="text-dark-bg" />
                </a>
                <a href="https://discord.gg/im0w" target="_blank" rel="noopener noreferrer" className="w-10 h-10 bg-neon-blue hover:bg-neon-blue/80 rounded-lg flex items-center justify-center transition-colors duration-300">
                  <MessageCircle size={20} className="text-dark-bg" />
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Newsletter;
