import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Bio from './components/Bio';
import Clips from './components/Clips';
import Blog from './components/Blog';
import Newsletter from './components/Newsletter';
import Footer from './components/Footer';
import GunBuilds from './pages/GunBuilds';
import SVGFog from './components/SVGFog';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time for smooth animations
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-dark-bg flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl font-gaming text-neon-blue animate-pulse mb-4">IM0W</div>
          <div className="w-16 h-16 border-4 border-neon-blue border-t-transparent rounded-full animate-spin mx-auto"></div>
        </div>
      </div>
    );
  }

  return (
    <Router>
      {/* SVG Grid should be here if not already */}
      {/* Fog overlays */}
      <div className="fixed bottom-0 left-0 w-[400px] h-[300px] pointer-events-none z-0">
        <SVGFog />
      </div>
      <div className="fixed top-0 right-0 w-[400px] h-[300px] pointer-events-none z-0 rotate-180">
        <SVGFog />
      </div>
      <Navbar />
      <main>
        <Routes>
          <Route path="/" element={
            <>
              <Hero />
              <Bio />
              <Clips />
              <Blog />
              <Newsletter />
            </>
          } />
          <Route path="/gun-builds" element={<GunBuilds />} />
        </Routes>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
